from string import lower

def rot1(s):
    t = lower(s)
    for i in range(len(t)):
        if t[i].isalpha():
            if t[i].isupper():
                t[i] = chr((ord(t[i]) - 65 + 26) % 26 + 65)
            else:
                t[i] = chr((ord(t[i]) - 97 + 26) % 26 + 97)
